```go
package main
import (
	"flag"
	"fmt"
	"io/fs"
	"os"
	"path/filepath"
	"regexp"
	"strings"
)
func ScanDocs(fsys fs.FS, dir string, re *regexp.Regexp, debug bool) error {
	return fs.WalkDir(fsys, dir, func(path string, d fs.DirEntry, err error) error {
		if err != nil {
			return err
		}
		if d.IsDir() && level(path, dir) == 4 {
			files, nonMatchingFiles, err := filterFiles(fsys, path, re, debug)
			if err != nil {
				return err
			}
			format, section, region, supplier, err := getParentDirs(path)
			if err != nil {
				return err
			}
			ProcessDocs(debug, format, section, region, supplier, files, nonMatchingFiles)
		}
		return nil
	})
}
func filterFiles(fsys fs.FS, dir string, re *regexp.Regexp, debug bool) ([]string, []string, error) {
	fileEntries, err := fs.ReadDir(fsys, dir)
	if err != nil {
		return nil, nil, err
	}
	matchingFiles := []string{}
	nonMatchingFiles := []string{}
	for _, entry := range fileEntries {
		if !entry.IsDir() {
			file := filepath.Join(dir, entry.Name())
			if re.MatchString(file) {
				matchingFiles = append(matchingFiles, file)
			} else {
				nonMatchingFiles = append(nonMatchingFiles, file)
			}
		}
	}
	return matchingFiles, nonMatchingFiles, nil
}
func level(path, root string) int {
	relPath, _ := filepath.Rel(root, path)
	return len(strings.Split(relPath, string(filepath.Separator))) - 1
}
func getParentDirs(path string) (string, string, string, string, error) {
	parent := filepath.Dir(path)
	supplier := filepath.Base(parent)
	parent = filepath.Dir(parent)
	region := filepath.Base(parent)
	parent = filepath.Dir(parent)
	section := filepath.Base(parent)
	parent = filepath.Dir(parent)
	format := filepath.Base(parent)
	return format, section, region, supplier, nil
}
func ProcessDocs(debug bool, format, section, region, supplier string, files, nonMatchingFiles []string) {
	if debug {
		fmt.Println("Non-matching files:", nonMatchingFiles)
	}
	// Your logic to process files here
	fmt.Printf("Format: %s, Section: %s, Region: %s, Supplier: %s\n", format, section, region, supplier)
	fmt.Println("Files:", files)
}
func main() {
	dirPtr := flag.String("dir", "./test-importe", "The directory to scan")
	debugPtr := flag.Bool("debug", false, "Enable debug mode")
	flag.Parse()
	re := regexp.MustCompile("(?i).*\\.(xlsx|xml|csv)")
	err := ScanDocs(os.DirFS("."), *dirPtr, re, *debugPtr)
	if err != nil {
		fmt.Println("Error:", err)
	}
}
```
