    // 按日期聚合数据
    const dataByDate = {};
    for (let i = 0; i < results.length; i++) {
      const result = results[i];
      const date = moment(result.date, 'YYYY-MM-DD').format('MM/DD/YYYY');
      const projectId = result.projectId;
      const count = Number(result.count);
      if (!dataByDate[date]) {
        dataByDate[date] = {};
      }
      if (!dataByDate[date][projectId]) {
        dataByDate[date][projectId] = 0;
      }
      dataByDate[date][projectId] += count;
    }
    // 将聚合数据写入同一文件中的新表格
    const csvWriter = createCsvWriter({
      path: 'api_logs_stats.csv',
      header: [
        { id: 'date', title: 'Date' },
        { id: 'projectId', title: 'Project ID' },
        { id: 'count', title: 'Count' },
      ],
      append: true,
    });
    const dataToWrite = [];
    for (const date in dataByDate) {
      for (const projectId in dataByDate[date]) {
        const count = dataByDate[date][projectId];
        dataToWrite.push({
          date: date,
          projectId: projectId,
          count: count,
        });
      }
    }
    csvWriter.writeRecords(dataToWrite)
      .then(() => console.log('The aggregated data was written to a new sheet in api_logs_stats.csv.'));
  });
