  return new Promise((resolve, reject) => {
    // 附加事件侦听器以侦听响应
    window.addEventListener("message", (event) => {
      if (event.data.type === "response") {
        resolve(event.data.payload);
      }
    });
    // 将消息发送到iframe
    iframe.contentWindow.postMessage({ type: "request", payload: message }, "*");
  });
}
