```python
class ScenarioStep:
    def __init__(self, step_name, sub_scenario=None, data_targets=[]):
        self.step_name = step_name
        self.sub_scenario = sub_scenario
        self.data_targets = data_targets
    def generate_text(self, level=0):
        indent = "  " * level
        step_text = indent + self.step_name
        if self.data_targets:
            step_text += " [{}]".format(", ".join(self.data_targets))
        if self.sub_scenario:
            step_text += "\n" + self.sub_scenario.generate_text(level + 1)
        return step_text
class Scenario:
    def __init__(self, steps):
        self.steps = steps
    def generate_text(self, level=0):
        scenario_text = "\n".join([step.generate_text(level) for step in self.steps])
        return scenario_text
class Message:
    def __init__(self, sender, text, data_target_state={}):
        self.sender = sender
        self.text = text
        self.data_target_state = data_target_state
class Conversation:
    def __init__(self, scenario):
        self.scenario = scenario
        self.messages = []
    def add_message(self, sender, text, data_target_state={}):
        message = Message(sender, text, data_target_state)
        self.messages.append(message)
    def get_history(self):
        history = [f"{message.sender}: {message.text}" for message in self.messages]
        return "\n".join(history)
