        windowWidth: 0,
        windowHeight: 0,
        pageHeight: 0,
        showNavigation: false,
        slack: 50 // Add a bit of slack for pages that are slightly bigger than the window height
      };
    },
    mounted() {
      this.windowWidth = window.innerWidth;
      this.windowHeight = window.innerHeight;
      this.pageHeight = document.documentElement.scrollHeight;
      if (this.pageHeight > this.windowHeight + this.slack) {
        this.showNavigation = true;
        window.addEventListener("resize", this.handleResize);
        window.addEventListener("scroll", this.handleScroll);
      }
    },
    beforeDestroy() {
      window.removeEventListener("resize", this.handleResize);
      window.removeEventListener("scroll", this.handleScroll);
    },
    methods: {
      handleResize() {
        this.windowWidth = window.innerWidth;
        this.windowHeight = window.innerHeight;
        this.pageHeight = document.documentElement.scrollHeight;
        if (this.pageHeight <= this.windowHeight + this.slack) {
          this.showNavigation = false;
          this.navTranslateY = 0;
        } else {
          this.showNavigation = true;
        }
      },
      handleScroll() {
        const currentScrollY = window.scrollY;
        const scrollDiff = currentScrollY - this.previousScrollY;
        const targetTranslateY = scrollDiff > 0 ? -200 : 0;
        const distanceToTarget = Math.abs(this.navTranslateY - targetTranslateY);
        const easing = distanceToTarget > 100 ? 0.2 : 0.5;
        if (currentScrollY < this.windowHeight || this.windowWidth < 1024 || !this.showNavigation) {
          this.navTranslateY = 0;
        } else {
          this.navTranslateY += (targetTranslateY - this.navTranslateY) * easing;
        }
        this.previousScrollY = currentScrollY;
      }
    }
  }
</script>
